    <style>
        /* Responsive layout for features section */
        .features-row {
            display: block;
        }

        @media (min-width: 992px) {
            .features-row {
                display: flex;
                align-items: flex-start;
                gap: 2rem;
            }

            .features-row .oti3 {
                flex: 1 1 0;
                order: 1;
            }

            .features-row .img-container2 {
                flex: 0 0 40%;
                order: 2;
                display: flex;
                justify-content: flex-end;
            }

            .features-row .img-container2 img {
                max-width: 100%;
                height: auto;
                display: block;
            }
        }
    </style>
